import './App.css';
import axios from "axios";
import React, { useState, useEffect } from "react";


const styles = {


}

const UserInfoField = (props) => {

  return (
    <div id="user-info-element">
      <div class="label">{props.label}</div>
      <div class="info">{props.info}</div>
    </div>

  );
}

const UserProfile = () => {

  const [userProfile, setUserProfile] = useState([]);

  const fetchUserProfile = () => {

    axios.get("http://localhost:8080/api/v1/users/1").then(res => {
      console.log(res);
      setUserProfile(res.data);
    });

  }

  useEffect(() => {
    fetchUserProfile();
  }, []);


  return (
    <div id="user-info-wrapper">
      <h1>User Information</h1>
      <UserInfoField label="Name" info={userProfile.userName} />
      <UserInfoField label="Surname" info={userProfile.surname} />
    </div>


  );

};


function App() {
  return (
    <div className="App">
      <UserProfile />
    </div>
  );
}

export default App;
