import './App.css';
import axios from "axios";
import React, { useState, useEffect } from "react";
import Accordion from ""

const User = () => {

  const [data, setData] = useState({})
  const [userId, setUserId] = useState(undefined);
  const [loading, setLoading] = useState(false);

  const inputRef = React.createRef()

  const getData = id => {
    const result = axios(
      'http://localhost:8080/api/v1/users/' + id,
    ).then((response) => {

      return response.data;
    }).catch((error) => {
      if (error.response.status === 404) {
        console.log("User not found!!!")
      }
    });

    return result;

  }

  const updateInput = () => {
    const inputText = inputRef.current.value;
    console.log("setting the user id: " + inputText);
    setUserId(inputText);
  }

  useEffect(() => {
    console.log("Enter UseEffect");
    console.log("UserID inside UseEffect: " + userId);

    const getUser = async () => {
      console.log("enter getUser");
      const userData = await getData(userId);
      console.log("User DATA IS: ");
      console.log(userData);
      if (userData) {
        console.log("enter userData")
        setData(userData);
      }
    }

    if (userId !== undefined) {
      getUser();
    }


  }, [userId]);

  let userInfoDisplay;

  userInfoDisplay = userId === undefined ?
    <div></div> :
    (<div className='userInfoDisplay pt-10'>
      <div className='flex flex-row space-x-7 pl-3'>
        <div className='text-[#7D7D7D]'>Name</div>
        <div className='text-[#7D7D7D]'>{data.userName === null ? null : data.userName}</div>
      </div>
      <div className='flex flex-row space-x-7 pl-3'>
        <div className='text-[#7D7D7D]'>Surname</div>
        <div className='text-[#7D7D7D]'>{data.surname === null ? null : data.surname}</div>
      </div>

      <div className="accounts pl-3 pt-3">
        <div>
          {data.accounts && data.accounts.map((account, index) => {
            return (
              <div>
                <p>Account {account.accountId} - Balance {account.amount}</p>
                <ol>
                  <div></div>
                </ol>
              </div>
            );
          })
          }
        </div>
      </div>
    </div>);

  return (<div className="userInformationSection">
    <div className='inputUserId pt-20'>
      <h1 className='text-2xl font-bold pl-3'>User Information</h1>
      <div className='pt-6'>
        <div className='pl-3'>User ID</div>
        <input ref={inputRef} className='bg-[#BDB7E7] rounded-full text-white text-xl border-transparent' />
        <button onClick={updateInput}>Click</button>
      </div>
    </div>
    {userInfoDisplay}



  </div>);

};



function App() {
  return (
    <div className="App pl-10 pt-7">
      <div className="createAccountSection">
        <h1 className='text-2xl font-bold pl-3'>Create new account</h1>
        <div className='flex flex-row space-x-40 pt-6'>
          <div>
            <div className='font-semibold pl-3'>User Id</div>
            <input className='bg-[#BDB7E7] rounded-full text-white text-xl border-transparent' />
          </div>
          <div>
            <div className='font-semibold pl-3'>Initial amount</div>
            <input className='bg-[#BDB7E7] rounded-full text-white text-xl border-transparent' />
          </div>
          <div className='bg-[#BDB7E7] rounded-full text-white text-xl border-transparent' >
            <p className='pt-2 pr-2 pl-2 pb-1'>ADD</p>
          </div>
        </div>
      </div>
      <User></User>

      <Accordion>
        <Accordion.Panel>
          <Accordion.Title>
            What is Flowbite?
          </Accordion.Title>
          <Accordion.Content>
            <p className="mb-2 text-gray-500 dark:text-gray-400">
              Flowbite is an open-source library of interactive components built on top of Tailwind CSS including buttons, dropdowns, modals, navbars, and more.
            </p>
            <p className="text-gray-500 dark:text-gray-400">
              Check out this guide to learn how to{' '}
              <a
                href="https://flowbite.com/docs/getting-started/introduction/"
                className="text-blue-600 hover:underline dark:text-blue-500"
              >
                get started
              </a>
              {' '}and start developing websites even faster with components on top of Tailwind CSS.
            </p>
          </Accordion.Content>
        </Accordion.Panel>
        <Accordion.Panel>
          <Accordion.Title>
            Is there a Figma file available?
          </Accordion.Title>
          <Accordion.Content>
            <p className="mb-2 text-gray-500 dark:text-gray-400">
              Flowbite is first conceptualized and designed using the Figma software so everything you see in the library has a design equivalent in our Figma file.
            </p>
            <p className="text-gray-500 dark:text-gray-400">
              Check out the{' '}
              <a
                href="https://flowbite.com/figma/"
                className="text-blue-600 hover:underline dark:text-blue-500"
              >
                Figma design system
              </a>
              {' '}based on the utility classes from Tailwind CSS and components from Flowbite.
            </p>
          </Accordion.Content>
        </Accordion.Panel>
        <Accordion.Panel>
          <Accordion.Title>
            What are the differences between Flowbite and Tailwind UI?
          </Accordion.Title>
          <Accordion.Content>
            <p className="mb-2 text-gray-500 dark:text-gray-400">
              The main difference is that the core components from Flowbite are open source under the MIT license, whereas Tailwind UI is a paid product. Another difference is that Flowbite relies on smaller and standalone components, whereas Tailwind UI offers sections of pages.
            </p>
            <p className="mb-2 text-gray-500 dark:text-gray-400">
              However, we actually recommend using both Flowbite, Flowbite Pro, and even Tailwind UI as there is no technical reason stopping you from using the best of two worlds.
            </p>
            <p className="mb-2 text-gray-500 dark:text-gray-400">
              Learn more about these technologies:
            </p>
            <ul className="list-disc pl-5 text-gray-500 dark:text-gray-400">
              <li>
                <a
                  href="https://flowbite.com/pro/"
                  className="text-blue-600 hover:underline dark:text-blue-500"
                >
                  Flowbite Pro
                </a>
              </li>
              <li>
                <a
                  href="https://tailwindui.com/"
                  rel="nofollow"
                  className="text-blue-600 hover:underline dark:text-blue-500"
                >
                  Tailwind UI
                </a>
              </li>
            </ul>
          </Accordion.Content>
        </Accordion.Panel>
      </Accordion>
    </div >
  );
}

export default App;
