import './App.css';
import axios from "axios";
import React, { useState, useEffect } from "react";


const styles = StyleSheet.create({
  container: {
    flex: 1,
    marginTop: 8,
    backgroundColor: "aliceblue",
  },
  box: {
    width: 50,
    height: 50,
  },
  row: {
    flexDirection: "row",
    flexWrap: "wrap",
  },
  button: {
    paddingHorizontal: 8,
    paddingVertical: 6,
    borderRadius: 4,
    backgroundColor: "oldlace",
    alignSelf: "flex-start",
    marginHorizontal: "1%",
    marginBottom: 6,
    minWidth: "48%",
    textAlign: "center",
  },
  selected: {
    backgroundColor: "coral",
    borderWidth: 0,
  },
  buttonLabel: {
    fontSize: 12,
    fontWeight: "500",
    color: "coral",
  },
  selectedLabel: {
    color: "white",
  },
  label: {
    textAlign: "center",
    marginBottom: 10,
    fontSize: 24,
  },
});


const UserInfoField = (props) => {

  return (
    <div id="user-info-element" style={styles.row}>
      <div class="label">{props.label}</div>
      <div class="info">{props.info}</div>
    </div>

  );
}

const UserProfile = () => {

  const [userProfile, setUserProfile] = useState([]);

  const fetchUserProfile = () => {

    axios.get("http://localhost:8080/api/v1/users/1").then(res => {
      console.log(res);
      setUserProfile(res.data);
    });

  }

  useEffect(() => {
    fetchUserProfile();
  }, []);


  return (
    <div id="user-info-wrapper">
      <h1>User Information</h1>
      <UserInfoField label="Name" info={userProfile.userName} />
      <UserInfoField label="Surname" info={userProfile.surname} />
    </div>


  );

};


function App() {
  return (
    <div className="App">
      <UserProfile />
    </div>
  );
}

export default App;
