import './App.css';
import axios from "axios";
import React, { useState, useEffect } from "react";

const User = () => {

  const [data, setData] = useState([])
  const [userId, setUserId] = useState(undefined)
  const [loading, setLoading] = useState(false);

  const inputRef = React.createRef()

  const getData = id => {
    const result = axios(
      'http://localhost:8080/api/v1/users/' + id,
    ).then(() => {

      console.log("works");
      console.log(result);
    }).catch((error) => {
      if (error.response.status === 404) {
        console.log("User not found!!!")
      }
    });

    return result.data;

  }

  const updateInput = () => {
    const inputText = inputRef.current.value;
    console.log("setting the user id: " + inputText);
    setUserId(inputText);
  }

  useEffect(() => {
    console.log("Enter UseEffect");
    console.log("UserID inside UseEffect: " + userId);
    if (userId !== undefined) {
      setLoading(true);
      const data = getData(userId);
      if(data) {
        set
      }

    }


  }, [userId]);

  let userInfoDisplay;

  userInfoDisplay = userId === undefined ?
    <div></div> :
    (<div className='userInfoDisplay pt-10'>
      <div className='flex flex-row space-x-7 pl-3'>
        <div className='text-[#7D7D7D]'>Name</div>
        <div className='text-[#7D7D7D]'>{data.userName === null ? null : data.userName}</div>
      </div>
      <div className='flex flex-row space-x-7 pl-3'>
        <div className='text-[#7D7D7D]'>Surname</div>
        <div className='text-[#7D7D7D]'>{data.surname === null ? null : data.surname}</div>
      </div>

      <div className="accounts pl-3 pt-3">
        <div>
          <div>Holaaaaaaaaaaaaaaaaaaa</div>
        </div>
      </div>
    </div>);

  return (<div className="userInformationSection">
    <div className='inputUserId pt-20'>
      <h1 className='text-2xl font-bold pl-3'>User Information</h1>
      <div className='pt-6'>
        <div className='pl-3'>User ID</div>
        <input ref={inputRef} className='bg-[#BDB7E7] rounded-full text-white text-xl border-transparent' />
        <button onClick={updateInput}>Click</button>
      </div>
    </div>
    {userInfoDisplay}


  </div>);

};



function App() {
  return (
    <div className="App pl-10 pt-7">
      <div className="createAccountSection">
        <h1 className='text-2xl font-bold pl-3'>Create new account</h1>
        <div className='flex flex-row space-x-40 pt-6'>
          <div>
            <div className='font-semibold pl-3'>User Id</div>
            <input className='bg-[#BDB7E7] rounded-full text-white text-xl border-transparent' />
          </div>
          <div>
            <div className='font-semibold pl-3'>Initial amount</div>
            <input className='bg-[#BDB7E7] rounded-full text-white text-xl border-transparent' />
          </div>
          <div className='bg-[#BDB7E7] rounded-full text-white text-xl border-transparent' >
            <p className='pt-2 pr-2 pl-2 pb-1'>ADD</p>
          </div>
        </div>
      </div>
      <User></User>

    </div>
  );
}

export default App;
