import './App.css';
import axios from "axios";
import React, { useState, useEffect } from "react";

const User = () => {

  const [data, setData] = useState({})
  const [userId, setUserId] = useState(undefined);
  const [loading, setLoading] = useState(false);

  const inputRef = React.createRef()

  const getData = id => {
    const result = axios(
      'http://localhost:8080/api/v1/users/' + id,
    ).then((response) => {

      return response.data;
    }).catch((error) => {
      if (error.response.status === 404) {
        console.log("User not found!!!")
      }
    });

    return result;

  }

  const updateInput = () => {
    const inputText = inputRef.current.value;
    console.log("setting the user id: " + inputText);
    setUserId(inputText);
  }

  useEffect(() => {
    console.log("Enter UseEffect");
    console.log("UserID inside UseEffect: " + userId);

    const getUser = async () => {
      console.log("enter getUser");
      const userData = await getData(userId);
      console.log("User DATA IS: ");
      console.log(userData);
      if (userData) {
        console.log("enter userData")
        setData(userData);
      }
    }

    if (userId !== undefined) {
      getUser();
    }


  }, [userId]);

  let userInfoDisplay;

  userInfoDisplay = userId === undefined ?
    <div></div> :
    (<div className='userInfoDisplay pt-10'>
      <div className='flex flex-row space-x-7 pl-3'>
        <div className='text-[#7D7D7D]'>Name</div>
        <div className='text-[#7D7D7D]'>{data.userName === null ? null : data.userName}</div>
      </div>
      <div className='flex flex-row space-x-7 pl-3'>
        <div className='text-[#7D7D7D]'>Surname</div>
        <div className='text-[#7D7D7D]'>{data.surname === null ? null : data.surname}</div>
      </div>

      <div className="accounts pl-3 pt-3">
        <div>
          {data.accounts && data.accounts.map((account, index) => {
            return (
              <div>
                <p>Account {account.accountId} - Balance {account.amount}</p>
                <ol>
                  <div></div>
                </ol>
              </div>
            );
          })
          }
        </div>
      </div>
    </div>);

  return (<div className="userInformationSection">
